Toggle class on element const element = document.querySelector(querySelector);if (element) {if (element.classList.contains(className)) {element.classList.remove(className);}else {element.classList.add(className);}return true;}else return false; JS_ToggleClassOnElement.mpk
Select all text on input focus Set this JavaScript action on the OnChange nanoflow
const activeElement = document.activeElement;if (activeElement.tagName === &#34;INPUT&#34;) activeElement.select(); JS_Input_SelectOnFocus.mpk
Scroll to element // BEGIN EXTRA CODEfunction scrollToElement(targetSelector, block, inline) {const elements = document.querySelectorAll(targetSelector);if (elements) {const element = elements[elements.length -1];if (element){console.debug(element);element.scrollIntoView({behavior: &#34;smooth&#34;,block: block,inline: inline});return true;}}return false;}// END EXTRA CODEif (scrollToElement(targetSelector, block, inline)) return;//Retry because sometimes the DOM element is not loaded yet. console.log(&#34;no element found&#34;);setTimeout(function() {scrollToElement(targetSelector, block, inline);},200); JS_ScrollToElement.mpk
Scroll to error const validationMessage = document.querySelector(&#34;.mx-validation-message&#34;);if(validationMessage) {scrollIntoView(validationMessage);return;}const alert = document.querySelector(&#34;.alert.alert-danger&#34;);if(alert) {scrollIntoView(alert);return;}console.warn(&#34;No validation message found.&#34;); JS_ScrollToError.mpk

